import axios from 'axios';
import React, { useState, useEffect } from 'react'
import jwt_decode from "jwt-decode";


const MyAccount =() => {
    const [edit, setEdit] = useState("");
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");
    const [subjects, setSubjects] = useState("");
    const [availability, setAvailability] = useState("");
    const [state, setState] = useState("");
    const [town, setTown] = useState("");


    const getAccount = async() =>{
        const tokenInfo = jwt_decode(localStorage.getItem("token"));
        // console.log(tokenInfo)
        const urlString = `http://localhost:3003/${tokenInfo.status}/${tokenInfo.statusId}`;
        try{
            const { data } = await axios.get(urlString);
            setLastName(data.lastName);
            setFirstName(data.firstName);
            setEmail(data.email);
            setState(data.state);
            setTown(data.town);
            setSubjects(data.studentSubjects)
            setAvailability(data.availability)
        }catch(e){
            console.log(e)
        }
    };

    // const submitInfo = async(event) => {
    //     event.preventDefault();
    //     const tokenInfo = jwt_decode(localStorage.getItem("token"));
    //     let newData = {'info': newInfo, 'subjects': subjects};
    //     if(!newInfo){
    //         console.log('no new info')
    //         newData['info'] = info;
    //     }
    //     // console.log(newData)
    //     const urlString = `http://localhost:3003/${tokenInfo.status}/${tokenInfo.statusId}`;
    //     try{
    //         const { data } = await axios.put(urlString, newData);
    //         // console.log(data);
    //     }catch(e){
    //         console.log(e)
    //     }
    //     setEdit(false)
    // }

    // const addSubject = (event) => {
    //     event.preventDefault();
    //     setSubjects([...subjects, newSubject]);
    // }

    // const addInfo = (event) => {
    //     event.preventDefault();
    //     setInfo(newInfo);
    //     setNewInfo(newInfo);
    // }

    const onClickAccount = () => {
        setEdit(true);
    }

    const onClickNoAccount = () => {
        setEdit(false);
    }

    const deleteSubject = (e) => {
        const {id} = e.target.parentElement;
        subjects.splice(id, 1)
        setSubjects([...subjects])
        console.log(subjects)
    }

    useEffect(() => {
        getAccount()
    }, []);

    return (
        <div className="container">
            <h1>{firstName}'s Page.</h1>
            <br/>
            <hr/>
            {!edit && <div className="row">
                <div className="col">
                    <h2>My Subjects: </h2>
                    {subjects && subjects.map(s => (
                        <div key={subjects.value}>
                            <p>{s}</p>
                        </div>
                    ))}               
                </div>
                <div className="col">
                    <h2>My Availability: </h2>
                    {availability && availability.map(s => (
                        <div key={Math.random() * 100000}>
                            <p>{s}</p>
                        </div>
                    ))}  
                </div>
                <div className="col">
                    <h2>My Account: </h2>
                    <div className="form-group">First name: {firstName}</div>
                    <div className="form-group">Last name: {lastName}</div>
                    <div className="form-group">Email: {email}</div>
                    <div className="form-group">State: {state}</div>
                    <div className="form-group">Town: {town}</div>
                </div>
            </div>}

            {!edit && <button className='ui button' onClick={onClickAccount}>Edit My Account</button>}        

            {edit && <form className="ui form">
            <div className="field">
                <div className="field">
                <label>First Name</label>
                <input 
                type='text' 
                name='first-name' 
                placeholder='First Name'
                value={firstName}
                onChange={(e) => setFirstName(e.target.value)}
                required
                />
                </div>
                <div className="field">
                <label>Last Name</label>
                <input 
                type='text' 
                name='last-name' 
                placeholder='Last Name'
                value={lastName}
                onChange={(e) => setLastName(e.target.value)}
                required
                />
                </div>
                <div className="field">
                <label>State</label>
                <input 
                type='text' 
                name='state' 
                placeholder='State'
                value={state}
                onChange={(e) => setState(e.target.value)}
                required
                />
                </div>
                <div className="field">
                <label>Town</label>
                <input 
                type='text' 
                name='town' 
                placeholder='Town'
                value={town}
                onChange={(e) => setTown(e.target.value)}
                required
                />
                </div>
                <div className='field'>
                <h2>My Subjects: </h2>
                    {subjects && subjects.map(s => (
                        <div key={subjects.value}>
                            <p>{s}
                            <button onClick={deleteSubject}>Delete</button>
                            </p>
                        </div>
                    ))}     
                </div>



                {/* <div className="field">
                    <label>Add a subject</label>
                    <input
                    type="text"
                    name="subject"
                    value={newSubject}
                    onChange={(e) => setNewSubject(e.target.value)}
                    />
                <button className='ui button' onClick={addSubject}>Update Subject</button>
                </div>                          */}

            </div>    
            </form>}
            <br/>
            { edit && <button className='ui button' onClick={onClickNoAccount}>Discard Change</button>}  

            {/* {edit && <form className="ui form" onSubmit={submitInfo}>
                <div className="field">
                    <label>Add a subject</label>
                    <input
                    type="text"
                    name="subject"
                    value={newSubject}
                    onChange={(e) => setNewSubject(e.target.value)}
                    />
                <button className='ui button' onClick={addSubject}>Update Subject</button>
                </div>

                <div className="field">
                    <label>Add About Me</label>
                    <textarea 
                    rows="3"
                    placeholder="Say something about you." 
                    value={newInfo}
                    onChange={(e) => setNewInfo(e.target.value)} 
                    />
                    <button className='ui button' onClick={addInfo}>Update About Me </button>
                </div>


            <div className="col"><button className='ui button' type='submit' style={{position: 'absolute', right: 50}}>Save Change </button></div>
            </form>} } */}
        </div>
    )
}

export default MyAccount;
