        <div className="container">
            <h1>{firstName}'s Page.</h1>
            <br/>
            <hr/>
            {!edit && <div className="row">
                <div className="col">
                    <h2>My Subjects: </h2>
                    {subjects && subjects.map(s => (
                        <div key={Math.random() * 100000}>
                            <p>{s}</p>
                        </div>
                    ))}               
                </div>
                <div className="col">
                    <h2>My Availability: </h2>
