import React, {useState} from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const SignUp = (prop) => {
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const history = useHistory();


    const submitInfo = async(event) =>{
      event.preventDefault();
      // check all input
      console.log(password)
      console.log(confirmPassword)
      if(password !== confirmPassword){
          return history.push('/signup',{noMatch: true})

      }
      try {
        const result = await axios.post('http://localhost:3003/signup',{
          'firstName': firstName,
          'lastName': lastName,
          'email': email,
          'password': password
        });
        if(result){
          console.log('succeed')
          history.push('/login')
        }else{
          console.log('failed')
        }
      }catch(e){
        console.log(e);
      }


    }

    return (
      <form className="ui form" onSubmit={submitInfo}>
        <div className="field">
          <label>Email</label>
          <input 
          className='form-control is-invalid'
          type='email' 
          name='email' 
          placeholder='triddle@slytherin.edu' 
          value={email} 
          onChange={(e) => setEmail(e.target.value)}
          required
          />
        </div>

        <div className="field">
          <label>Password</label>
          <input 
          type='password' 
          name='password'  
          value={password} 
          onChange={(e) => setPassword(e.target.value)}
          required
          />
        </div>

        <div className="field">
          <label>Confirm Password</label>
          { prop.noMatch && <div
            className="ui pointing below prompt label"
            id="form-input-control-error-email-error-message"
            role="alert"
            aria-atomic="true"
          >
            Please enter a valid email address
          </div>}

          <input 
          // className={passwordMatch}
          type='password' 
          name='confirm-password'  
          value={confirmPassword} 
          onChange={(e) => setConfirmPassword(e.target.value)}
          required
          />
        </div>

        <div className="field">
          <label>First Name</label>
          <input 
          type='text' 
          name='first-name' 
          placeholder='First Name'
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
          required
          />
        </div>

        <div className="field">
          <label>Last Name</label>
          <input 
          type='text' 
          name='last-name' 
          placeholder='Last Name'
          value={lastName}
          onChange={(e) => setLastName(e.target.value)}
          required
          />
        </div>

        <div className="field">
          <div className='ui checkbox'>
          <input type='checkbox' tabIndex='0' required/>
          <label>I solemnly swear that I am up to no good. </label>
          </div>
        </div>
        <button className='ui button' type='submit'>Sign Up</button>
      </form>
    );
}

export default SignUp;
