import React, {useState} from 'react';
import App from './App';

const Demo = () => {
    const [subjects, setSubjects] = useState([
        {
            name: 'Flying'  
        }, 
        {
            name: 'Potion' 
        }
    ])


    const [subject, setSubject] = useState({});
    const onChange = (event) => {
        setSubject({[event.target.name]: event.target.value});
    }

    const onSubmit = (event) =>{
        event.preventDefault();
        if(subject.name.trim() ==='') return;

        const newSubject = {
            name: subject.name.trim(),
        }

        setSubjects([...subjects, newSubject]);
    }

    return (
        <div className="container">
            <h1>Hello World. Im a demo page</h1>
            <div className="row">
                <div className="col">
                    <h2>Add a subject: </h2>
                    <hr/>
                    <form onSubmit={onSubmit}>
                        <div className="form-group">
                            <input
                            type="text"
                            className="form-control"
                            name="subject" 
                            placeholder="subject..."
                            value={subject.name}
                            onChange={onChange}
                            />
                        </div>
                        {/* <div className="form-group">
                            <input
                            type="text"
                            className="form-control"
                            name="lastName" 
                            placeholder="last name..."
                            value={subject.lastName}
                            onChange={onChange}
                            />
                        </div> */}
                        <button className="btn btn-success" type="submit">Add Subject</button>
                    </form>
                </div>
                <div className="col">
                    <h2>Subjects: </h2>
                    <hr/>
                    {subjects.map(s => (
                        <div key={Math.random() * 100000}>
                            <p>{s.name}</p>
                        </div>
                    ))}
                </div>
            </div>
            <div className="row">
                <div className="col">
                    <h2>Add Introduction: </h2>
                    <hr/>

                </div>
                <div className="col">
                    <h2>About Me: </h2>
                    <hr/>
                    <p>I am a muggle.</p>

                </div>
            </div>
        </div>
    )
}

export default Demo;
