import React, {useState} from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const SignUp = () => {
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const history = useHistory();

    const [passwordMatch, setPasswordMatch] = useState("form-check-input is-valid")

    const submitInfo = async(event) =>{
      event.preventDefault();
      // check all input
      if(password !== confirmPassword){
          setPasswordMatch("form-check-input is-invalid")
      }
      try {
        const result = await axios.post('http://localhost:3003/signup',{
          'firstName': firstName,
          'lastName': lastName,
          'email': email,
          'password': password
        });
        if(result){
          console.log('succeed')
          history.push('/login')
        }else{
          console.log('failed')
        }
      }catch(e){
        console.log(e);
      }


    }

    return (
      <form className="ui form" onSubmit={submitInfo}>
        <div className="field">
          <label>Email</label>
          <input 
          className='form-control is-invalid'
          type='email' 
          name='email' 
          placeholder='triddle@slytherin.edu' 
          value={email} 
          onChange={(e) => setEmail(e.target.value)}
          required
          />

        </div>
        <div className="field">
          <label>Password</label>
          <input 
          type='password' 
          name='password'  
          value={password} 
          onChange={(e) => setPassword(e.target.value)}
          required
          />

        </div>
        <div className="field">
          <label>Confirm Password</label>
          <input 
          className={passwordMatch}
          type='password' 
          name='confirm-password'  
          value={confirmPassword} 
          onChange={(e) => setConfirmPassword(e.target.value)}
          required
          />
          <div className="invalid-feedback">
            Password doesn't match.
          </div>
        </div>
        <div className="field">
          <label>First Name</label>
          <input 
          type='text' 
          name='first-name' 
          placeholder='First Name'
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
          required
          />

        </div>
        <div className="field">
          <label>Last Name</label>
          <input 
          type='text' 
          name='last-name' 
          placeholder='First Name'
          value={lastName}
          onChange={(e) => setLastName(e.target.value)}
          required
          />

        </div>
        <div className="field">
          <div className='ui checkbox'>
          <input type='checkbox' tabIndex='0' required/>
          <label>I solemnly swear that I am up to no good. </label>
          </div>
        </div>
        <button className='ui button' type='submit'>Sign Up</button>
      </form>
    );
}

export default SignUp;
