import React, { useState, useEffect } from 'react';
import { Menu } from 'semantic-ui-react';
import { Link } from 'react-router-dom';
import jwt_decode from "jwt-decode";

function Navigation() {
    // check pathname and set active in nav bar
    const pathname = window.location.pathname;
    const path = pathname ==='/' ? 'home': pathname.substr(1);
    const [activeItem, setActiveItem ] = useState(path); 
    const [returnUser, setReturnUser] = useState(false)
    const handleItemClick = (e, { name }) => setActiveItem(name);
    useEffect(() => {
      const token = localStorage.getItem("token");
      if (token){
        const tokenInfo = jwt_decode(localStorage.getItem(true))
        if(tokenInfo.exp > Date.now() / 1000){
          setReturnUser(true);
        }
      }
    },[]);

    return (
      <div>
        <Menu pointing secondary size='massive' color='teal'>
          <Menu.Item
            name='home'
            active={activeItem === 'home'}
            onClick={handleItemClick}
            as={Link}
            to='/'
          />
          { 
            returnUser && <Menu.Menu position='right'>
              <Menu.Item
                name='mypage'
                active={activeItem === 'mypage'}
                onClick={handleItemClick}
                as={Link} 
                to='/demo'
              />
              <Menu.Item
              name='logout'
              active={activeItem === 'logout'}
              onClick={handleItemClick}
              as={Link}
              to='/logout'
            />
          </Menu.Menu>
          }

          {
            !returnUser && <Menu.Menu position='right'>
              <Menu.Item
                name='signup'
                active={activeItem === 'signup'}
                onClick={handleItemClick}
                as={Link} 
                to='/signup'
              />
              <Menu.Item
              name='login'
              active={activeItem === 'login'}
              onClick={handleItemClick}
              as={Link}
              to='/login'
            />
            </Menu.Menu>
          }

        </Menu>
      </div>
    )

  }


export default Navigation;
