import React, { useState, useEffect, useContext } from 'react';
import { Menu } from 'semantic-ui-react';
import { Link } from 'react-router-dom';
import UserContext from './context/UserContext';
import jwt_decode from "jwt-decode";


function Navigation() {
    // check pathname and set active in nav bar
    // const pathname = window.location.pathname;
    // const path = pathname ==='/' ? 'home': pathname.substr(1);
    // const [activeItem, setActiveItem ] = useState(path); 
    // const handleItemClick = (e, { name }) => setActiveItem(name);

    const {token, setToken} = useContext(UserContext);
    const [ personal, setPersonal ] = useState("");
    useEffect(() => {
      if(token){
        try{
          const tokenInfo = jwt_decode(token);
          const exp = tokenInfo.exp;
          if (exp < Date.now() / 1000){
            localStorage.clear("token");
            setToken(null);
          }
          console.log(tokenInfo)
          if (tokenInfo.status === 'totors'){
            setPersonal("/TutorInfo")
          }else{
            setPersonal("/myaccount")
          }
        }catch(e){
          setToken(null);
          localStorage.clear("token");
          console.log(e);
        }
      }
    });

    // useEffect(() => {
    //   if (token){
    //     setVisitor(false);
    //     const tokenInfo = jwt_decode(localStorage.getItem("token"));
    //     if(tokenInfo){
    //       setVisitor(false);
    //       const statusId = tokenInfo.statusId;
    //       const status = tokenInfo.status;
    //       const url = `/${status}/${statusId}`
    //       setUserUrl(url); 
    //       // console.log(userUrl);
    //       // console.log(`im in nav, and user email is ${userEmail}`);
    //     }
    //   }
    // },[token, userUrl]); 

    return (
      <div>
        <Menu pointing secondary size='massive' color='teal'>
          <Menu.Item
            name='home'
            // active={activeItem === 'home'}
            // onClick={handleItemClick}
            as={Link}
            to='/'
          />
          { 
            token && <Menu.Menu position='right'>
              <Menu.Item
                name='My Page'
                // active={activeItem === 'My Page'}
                // onClick={handleItemClick}
                as={Link}
                to='/myaccount'
              />
              <Menu.Item
              name='logout'
              // active={activeItem === 'logout'}
              // onClick={handleItemClick}
              as={Link}
              to='/logout'
            />
          </Menu.Menu>
          } 
          {
            !token && <Menu.Menu position='right'>
              <Menu.Item
                name='signup'
                // active={activeItem === 'signup'}
                // onClick={handleItemClick}
                as={Link} 
                to='/signup'
              />
              <Menu.Item
              name='login'
              // active={activeItem === 'login'}
              // onClick={handleItemClick}
              as={Link}
              to='/login'
            />
            </Menu.Menu>
          }

        </Menu>
      </div>
    )

  }


export default Navigation;
